import React, { Component } from "react";
import { Button, FormGroup, FormControl, FormLabel } from "react-bootstrap";
//import Form from 'react-bootstrap/Form'
//import Button from 'react-bootstrap/Button'
//import Bootstrap from "react-bootstrap";



import "./index.css";

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      username: ""
    };
  }

  validateForm() {
    return this.state.email.length > 0 && this.state.password.length > 0;

  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  handleSubmit = event => {
    event.preventDefault();
//put your logic here put the post in here? 
    console.log('currently the email is' + this.state.email);
    console.log('the username is ' + this.state.username); 
    console.log('the password is ' + this.state.password );
//okay everything has been recieved so now make a post route




  }

  render() {
    return (
      <div className="Login">
        <form onSubmit={this.handleSubmit}>

        <FormGroup controlId="username" bsSize="large">
            <FormLabel>Username</FormLabel>
            <FormControl
              autoFocus
              type="username"
              value={this.state.username}
              onChange={this.handleChange}
            />
          </FormGroup>

          <FormGroup controlId="email" bsSize="large">
            <FormLabel>Email</FormLabel>
            <FormControl
              autoFocus
              type="email"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large">
            <FormLabel>Password</FormLabel>
            <FormControl
              value={this.state.password}
              onChange={this.handleChange}
              type="password"
            />
          </FormGroup>
          <Button
            block
            bsSize="large"
            disabled={!this.validateForm()}
            type="submit"
          >
            Login
          </Button>
        </form>
      </div>
    );
  }
}
